            function addLoadEvent(fun) {
              if (window.addEventListener) {
                window.addEventListener("load", fun, false);
              } else if (window.attachEvent) {
                window.attachEvent("onload", fun);
              } else {
                var oldonload = window.onload;
                window.onload = function() {
                    if (oldonload) {
                        oldonload();
                    }
                    fun();
                }
              }
            }

